function fetchBookData(){const t=document.getElementById("search-input").value,e=`https://openlibrary.org/search.json?title=${encodeURIComponent(t)}`;fetch(e).then((t=>t.json())).then((t=>{displayBookData(t)})).catch((t=>{console.log("Error fetching book data:",t)}))}function displayBookData(t){const e=document.getElementById("results");if(e.innerHTML="",t.docs&&t.docs.length>0)t.docs.forEach((t=>{const o=t.title||"Title not available",n=t.key,c=document.createElement("div");c.classList.add("book");const a=document.createElement("h3");a.textContent=o,a.addEventListener("click",(()=>{fetchBookDescription(n,c)})),c.appendChild(a),e.appendChild(c)}));else{const t=document.createElement("p");t.textContent="No results found.",e.appendChild(t)}}function fetchBookDescription(t,e){fetch(`https://openlibrary.org${t}.json`).then((t=>t.json())).then((t=>{displayBookDescription(t.description||"Description not available",e)})).catch((t=>{console.log("Error fetching book description:",t)}))}function displayBookDescription(t,e){const o=document.createElement("p");o.textContent=t;const n=document.createElement("div");n.classList.add("card"),n.appendChild(o),e.appendChild(n)}const searchButton=document.getElementById("search-button");searchButton.addEventListener("click",fetchBookData);